#!/bin/bash

# Create directory for icons if it doesn't exist
mkdir -p icons

# Generate icon files using base64-encoded PNGs
# These are simple "CC" (Caption Copy) text icons with different sizes

# Icon 16x16
cat > icons/icon16.png.base64 << 'EOL'

EOL

# Icon 32x32
cat > icons/icon32.png.base64 << 'EOL'

EOL

# Icon 48x48
cat > icons/icon48.png.base64 << 'EOL'

EOL

# Icon 128x128
cat > icons/icon128.png.base64 << 'EOL'

EOL

# Convert base64 to PNG files
base64 -d icons/icon16.png.base64 > icons/icon16.png
base64 -d icons/icon32.png.base64 > icons/icon32.png
base64 -d icons/icon48.png.base64 > icons/icon48.png
base64 -d icons/icon128.png.base64 > icons/icon128.png

# Remove the base64 files
rm icons/*.base64

echo "Icon files created successfully."